using System.Collections; using System.Collections.Generic; using UnityEngine;  public class Speed : MonoBehaviour { 	//触れたら、力を加える（加速）AddForceを使用、Vector3（0,10,30）とする 	void OnTriggerEnter(Collider other){ 		other.gameObject.GetComponent<Rigidbody>().AddForce(new Vector3(0,10,10), ForceMode.VelocityChange); 	} }